extern alias SC;
using System;
using System.Drawing;
using System.IO;
using System.Linq;
using EVESharpCore.Framework.DirectX.Common;
using SC::SharedComponents.Py.D3DDetour;
using SharpDX;
using SharpDX.D3DCompiler;
using SharpDX.Direct2D1;
using SharpDX.Direct3D11;
using SharpDX.DirectWrite;
using SharpDX.DXGI;
using SharpDX.IO;
using SharpDX.Mathematics.Interop;
using SharpDX.Text;
using SharpDX.WIC;
using SharpDX.Win32;
using BitmapInterpolationMode = SharpDX.Direct2D1.BitmapInterpolationMode;
using Buffer = SharpDX.Direct3D11.Buffer;
using Device2 = SharpDX.DXGI.Device2;
using FillMode = SharpDX.Direct3D11.FillMode;
using InputElement = SharpDX.Direct3D11.InputElement;
using PixelFormat = SharpDX.Direct2D1.PixelFormat;

namespace EVESharpCore.Framework.DirectX
{
    public class DirectDraw
    {
        public bool IsInitialized { get; set; }

        private SharpDX.Direct2D1.Device _d2dDevice;
        private SharpDX.Direct2D1.DeviceContext _d2dContext;
        private SharpDX.DirectWrite.Factory _fontFactory;
        private static SharpDX.Mathematics.Interop.RawColor4 black = new SharpDX.Mathematics.Interop.RawColor4(0, 0, 0, 100);
        private static SharpDX.Mathematics.Interop.RawColor4 white = new SharpDX.Mathematics.Interop.RawColor4(255, 255, 255, 100);
        private BitmapProperties1 _properties;
        private TextFormat _textFormat;
        private Bitmap1 _d2dTarget;
        private DirectEve _directEve;
        private Surface _backBuffer;
        private SharpDX.DXGI.Device2 _device2;
        private SharpDX.Direct2D1.Bitmap1 _bitmap1;
        private SharpDX.Direct3D11.DeviceContext1 _d3dDeviceContext1;
        private int _width;
        private int _height;

        private static bool IS_DISABLED = true;

        public DirectDraw(DirectEve d)
        {
            _directEve = d;
        }

        private void Log(string s)
        {
            _directEve.Log(s);
        }

        public void Initialize()
        {

            SwapChain swapChain = (SharpDX.DXGI.SwapChain)_directEve.SwapChainPtr;

            _fontFactory = new SharpDX.DirectWrite.Factory();
            var device = swapChain.GetDevice<SharpDX.Direct3D11.Device>();
            var device1 = swapChain.GetDevice<SharpDX.Direct3D11.Device1>();

            _height = swapChain.Description.ModeDescription.Height;
            _width = swapChain.Description.ModeDescription.Width;
            _device2 = device.QueryInterface<SharpDX.DXGI.Device2>();


            //_dxgiAdapter = _device2.Adapter;
            //SharpDX.DXGI.Factory2 dxgiFactory2 = _dxgiAdapter.GetParent<SharpDX.DXGI.Factory2>();



            if (!device.CreationFlags.HasFlag(DeviceCreationFlags.BgraSupport))
            {
                Log($"No BgraSupport support?");
            }

            _d2dDevice = new SharpDX.Direct2D1.Device(_device2);
            _d2dContext = new SharpDX.Direct2D1.DeviceContext(_d2dDevice, SharpDX.Direct2D1.DeviceContextOptions.None);
            _d3dDeviceContext1 = device.ImmediateContext.QueryInterface<SharpDX.Direct3D11.DeviceContext1>();


            float dpiX, dpiY;
            using (Graphics graphics = Graphics.FromHwnd(IntPtr.Zero))
            {
                dpiX = graphics.DpiX;
                dpiY = graphics.DpiY;
            }

            _properties = new BitmapProperties1(new PixelFormat(SharpDX.DXGI.Format.B8G8R8A8_UNorm, SharpDX.Direct2D1.AlphaMode.Ignore), dpiX, dpiY, BitmapOptions.Target | BitmapOptions.CannotDraw);
            _textFormat = new TextFormat(_fontFactory, "Segoe UI", 36.0f);
            _backBuffer = swapChain.GetBackBuffer<Surface>(0);

            _d2dTarget = new Bitmap1(_d2dContext, _backBuffer, _properties);

            ImagingFactory imagingFactory = new ImagingFactory();
            //NativeFileStream fileStream = new NativeFileStream(@"D:\4.png",
            //    NativeFileMode.Open, NativeFileAccess.Read);

            var img =
                "";

            byte[] byteArray = Convert.FromBase64String(img);
            MemoryStream stream = new MemoryStream(byteArray);
            BitmapDecoder bitmapDecoder = new BitmapDecoder(imagingFactory, stream, DecodeOptions.CacheOnDemand);
            BitmapFrameDecode frame = bitmapDecoder.GetFrame(0);

            FormatConverter converter = new FormatConverter(imagingFactory);
            converter.Initialize(frame, SharpDX.WIC.PixelFormat.Format32bppPRGBA);
            _bitmap1 = SharpDX.Direct2D1.Bitmap1.FromWicBitmap(_d2dContext, converter);

            _height = swapChain.Description.ModeDescription.Height;
            _width = swapChain.Description.ModeDescription.Width;
            Log($"DirectWrite init. Height {_height} Width {_width}");


            IsInitialized = true;
        }

        public void Resize(object s, EventArgs e)
        {

            var args = (D3DResizeEventArgs)e;
            Log($"Resize called. Height [{args.Height}] Width [{args.Width}]");
            this._width = args.Width;
            this._height = args.Height;
            Dispose();
        }

        public void Dispose()
        {
            try
            {

                if (IS_DISABLED)
                    return;

                IsInitialized = false;
                _d2dDevice.Dispose();
                _d2dContext.Dispose();
                _backBuffer.Dispose();
                _d2dTarget.Dispose();
                _textFormat.Dispose();
                _fontFactory.Dispose();
                _bitmap1.Dispose();
                _d3dDeviceContext1.Dispose();
            }
            catch (Exception e)
            {
                Console.WriteLine(e);
            }
        }

        public void OnFrame()
        {

            if (IS_DISABLED)
                return;

            if (!IsInitialized)
            {
                Initialize();
                return;
            }

            // Set the Direct2D drawing target.
            _d2dContext.Target = _d2dTarget;

            var whiteBrush = new SolidColorBrush(_d2dContext, white);
            var blackBrush = new SolidColorBrush(_d2dContext, black);

            foreach (var e in _directEve.Entities.Where(e => e.IsPlayer))
            {
                //var pos = _directEve?.ActiveShip?.Entity?.ScreenPosVec3;
                var pos = e.ScreenPos;
                var diameter = e.EstimatedPixelDiameterWithChildren;
                //Log($"{pos}");

                if (diameter > 400)
                    continue;


                if (pos != null)
                {

                    //var factor = pos.Value.Z / 7000;
                    _d2dContext.BeginDraw();

                    RawRectangleF rect = new RawRectangleF()
                    {
                        Top = (float)pos.Value.Y - (float)diameter,
                        Bottom = (float)pos.Value.Y + (float)diameter,
                        Left = (float)pos.Value.X - (float)diameter,
                        Right = (float)pos.Value.X + (float)diameter,
                        //Top = (float)pos.Value.Y + _height / 2 - (float)diameter,
                        //Bottom = (float)pos.Value.Y + _height / 2 + (float)diameter,
                        //Left = (float)pos.Value.X + _width / 2 - (float)diameter,
                        //Right = (float)pos.Value.X + _width / 2 + (float)diameter,
                    };

                    _d2dContext.DrawBitmap(_bitmap1, rect, 1, BitmapInterpolationMode.Linear);

                    //Log($"Top {rect.Top} Bottom {rect.Bottom} Left {rect.Left} Right {rect.Right}");
                    //_d2dContext.FillRectangle(rect, whiteBrush);
                    //_d2dContext.DrawText("IT'S ME", _textFormat, rect, whiteBrush, DrawTextOptions.Clip);
                    _d2dContext.EndDraw();

                }
            }
        }
    }
}
